INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432630, 'Dextropropoxyphene', 'Propranolol', 'Moderate', 'Propoxyphene may increase the serum levels of some oral beta-blockers. The proposed mechanism is inhibition of CYP450 2D6 first-pass metabolism and decreased hepatic clearance. Data are available for metoprolol and propranolol only; however, other hepatically metabolized beta-blockers may also be affected. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'DDInter', 1767317476, 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes when propoxyphene is added to the patient''s medical regimen, and for decreased beta-blockade when propoxyphene is deleted from the regimen. A reduction in beta-blocker dosage may necessary.', 'Metabolism', 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes when propoxyphene is added to the patient''s medical regimen, and for decreased beta-blockade when propoxyphene is deleted from the regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
