INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432689, 'Dextropropoxyphene', 'Thiethylperazine', 'Major', 'Concomitant use of propoxyphene and phenothiazines may result in additive central nervous system (CNS) depressant effects. Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', 'DDInter', 1767317476, 'Caution is advised if propoxyphene is prescribed with phenothiazines, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Dosage reductions may be required. Patients should be warned not to exceed recommended dosages, to avoid alcohol, and to notify their physician if they experience symptoms of toxicity such as lethargy, excessive sedation, dizziness, syncope, seizures, and/or irregular heartbeat. In addition, they should avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is advised if propoxyphene is prescribed with phenothiazines, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', NULL, NULL, 'Major', 'pharmacodynamic');
