INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432694, 'Dextropropoxyphene', 'Ticagrelor', 'Moderate', 'Coadministration with opioid agonists may delay and reduce the absorption of orally administered P2Y12 inhibitors (e.g., clopidogrel, prasugrel, ticagrelor). The proposed mechanism may involve opioid-mediated slowed gastric emptying.', 'DDInter', 1767317476, 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists. In acute coronary syndrome patients who require an opioid agonist, the use of a parenteral antiplatelet agent, such as cangrelor, should be considered.', 'Absorption', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432695, 'Dextropropoxyphene', 'Timolol', 'Moderate', 'Propoxyphene may increase the serum levels of some oral beta-blockers. The proposed mechanism is inhibition of CYP450 2D6 first-pass metabolism and decreased hepatic clearance. Data are available for metoprolol and propranolol only; however, other hepatically metabolized beta-blockers may also be affected. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'DDInter', 1767317476, 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes when propoxyphene is added to the patient''s medical regimen, and for decreased beta-blockade when propoxyphene is deleted from the regimen. A reduction in beta-blocker dosage may necessary.', 'Metabolism', 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes when propoxyphene is added to the patient''s medical regimen, and for decreased beta-blockade when propoxyphene is deleted from the regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
