INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432716, 'Dextropropoxyphene', 'Trimipramine', 'Major', 'Concomitant use of propoxyphene and tricyclic and tetracyclic antidepressants (TCAs) may result in additive central nervous system (CNS) depressant effects. Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', 'DDInter', 1767317476, 'Caution is advised if propoxyphene is prescribed with TCAs, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Serum TCA levels should be monitored more closely following the addition or withdrawal of propoxyphene, and the TCA dosage adjusted as necessary.', 'Synergism', 'Caution is advised if propoxyphene is prescribed with TCAs, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', NULL, NULL, 'Major', 'pharmacodynamic');
