INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432767, 'Propranolol', 'Methacholine', 'Major', 'Patients who are concurrently receiving beta blockers may have exaggerated or prolonged responses (i.e., bronchoconstriction) to methacholine challenge and may not respond readily to treatment. Beta-2-adrenergic blockade increases airway resistance. Ophthalmically applied beta blockers undergo some systemic absorption and potentially may also interact.', 'DDInter', 1767317476, 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', 'Others', 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
