INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432776, 'Rifampicin', 'Propranolol', 'Moderate', 'Rifampin may decrease serum levels and effects of some oral beta-blockers. The proposed mechanism is increased CYP450 oxidative metabolism of beta-blockers that are hepatically metabolized. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'DDInter', 1767317476, 'Patients should be monitored for clinical response and tolerance when rifampin is added to or discontinued from a beta-blocker regimen. The beta-blocker dosage may require adjustment.', 'Metabolism', 'Patients should be monitored for clinical response and tolerance when rifampin is added to or discontinued from a beta-blocker regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
