INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432851, 'Edrophonium', 'Succinylcholine', 'Moderate', 'Acetylcholinesterase inhibitors may prolong and/or enhance the muscle relaxation effects of succinylcholine by blocking the activity of plasma cholinesterase, the enzyme responsible for the hydrolysis of succinylcholine. Prolonged neuromuscular blockade and respiratory depression may occur.', 'DDInter', 1767317476, 'It may be appropriate to discontinue acetylcholinesterase inhibitor therapy for a few days before the procedure depending on the needs of the patient. Otherwise, the patient should be monitored closely for increased pharmacologic effects of succinylcholine. Peripheral nerve stimulators are recommended for accurate assessment of neuromuscular blockade in patients who have been taking pyridostigmine.', 'Synergism', 'It may be appropriate to discontinue acetylcholinesterase inhibitor therapy for a few days before the procedure depending on the needs of the patient.', NULL, NULL, 'Moderate', 'pharmacodynamic');
