INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432862, 'Galantamine', 'Succinylcholine', 'Moderate', 'Acetylcholinesterase inhibitors may prolong and/or enhance the muscle relaxation effects of succinylcholine by blocking the activity of plasma cholinesterase, the enzyme responsible for the hydrolysis of succinylcholine. Prolonged neuromuscular blockade and respiratory depression may occur.', 'DDInter', 1767317476, 'It may be appropriate to discontinue acetylcholinesterase inhibitor therapy for a few days before the procedure depending on the needs of the patient. Otherwise, the patient should be monitored closely for increased pharmacologic effects of succinylcholine. Peripheral nerve stimulators are recommended for accurate assessment of neuromuscular blockade in patients who have been taking pyridostigmine.', 'Synergism', 'It may be appropriate to discontinue acetylcholinesterase inhibitor therapy for a few days before the procedure depending on the needs of the patient.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1432863, 'Gentamicin', 'Succinylcholine', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', 'DDInter', 1767317476, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, NULL, 'Major', 'pharmacodynamic');
