INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181613, 'Aminophylline', 'Methimazole', 'Moderate', 'Clearance of theophylline and related agents depends upon thyroid function. In the hyperthyroid state, clearance may be enhanced. In the hypothyroid state, clearance may be reduced. Theophylline clearance may decrease and the risk of toxicity may increase when hyperthyroid patients become euthyroid.', NULL, 'The patient and theophylline levels should be closely monitored while achieving a euthyroid state and when an antithyroid agent is added, discontinued, or the dosage is changed.', NULL, 'The patient and theophylline levels should be closely monitored while achieving a euthyroid state and when an antithyroid agent is added, discontinued, or the dosage is changed. Theophylline dosage adjustments may be required. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms, or any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181827/', '[1] Aderka D, Shavit G, Garfinkel D, et al "Life-threatening theophylline intoxication in a hypothyroid patient." Respiration 44 (1983): 77-80[2] Bauman JH, Teichman S, Wible DA "Increased theophylline clearance in a patient with hyperthyroidism." Ann Allergy 52 (1984): 94-6[3] "Product Information. Tapazole (methimazole)." King Pharmaceuticals Inc, Bristol, TN.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181614, 'Anisindione', 'Methimazole', 'Moderate', 'Anticoagulation effects may be increased or decreased with the concomitant use of antithyroid drugs and vitamin K antagonists. The proposed mechanism may relate to the effects on coagulation by a hyper- or hypothyroid state and/or the effects on vitamin K activity by antithyroid drugs. Antithyroid drugs may induce anti-vitamin K activity and thus intensify the effects of vitamin K antagonists.', NULL, 'Bleeding parameters (e.', NULL, 'Bleeding parameters (e.g., INR and PT) should be monitored closely in patients on anticoagulant therapy with vitamin K antagonists who are coadministered antithyroid therapy, particularly when concomitant treatment is started, discontinued, or with any dose adjustments. Dose adjustments of either medicine may be required. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181828/', '[1] Gotta AW, Sullivan CA, Seaman J, Jean-Gilles B "Prolonged intraoperative bleeding caused by propylthiouracil-induced hypoprothrombinemia." Anesthesiology 37 (1972): 562-3[2] Self T, Weisburst M, Wooten E, Straughn A, Oliver J "Warfarin-induced hypoprothrombinemia: potentiation by hyperthyroidism." JAMA 231 (1975): 1165-6[3] Cerner Multum, Inc "ANVISA Bulário Eletrônico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (20