INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181619, 'Idelalisib', 'Methimazole', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181833/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181620, 'Methimazole', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181834/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181621, 'Oxtriphylline', 'Methimazole', 'Moderate', 'Clearance of theophylline and related agents depends upon thyroid function. In the hyperthyroid state, clearance may be enhanced. In the hypothyroid state, clearance may be reduced. Theophylline clearance may decrease and the risk of toxicity may increase when hyperthyroid patients become euthyroid.', NULL, 'The patient and theophylline levels should be closely monitored while achieving a euthyroid state and when an antithyroid agent is added, discontinued, or the dosage is changed.', NULL, 'The patient and theophylline levels should be closely monitored while achieving a euthyroid state and when an antithyroid agent is added, discontinued, or the dosage is changed. Theophylline dosage adjustments may be required. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms, or any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181835/', '[1] Aderka D, Shavit G, Garfinkel D, et al "Life-threatening theophylline intoxication in a hypothyroid patient." Respiration 44 (1983): 77-80[2] Bauman JH, Teichman S, Wible DA "Increased theophylline clearance in a patient with hyperthyroidism." Ann Allergy 52 (1984): 94-6[3] "Product Information. Tapazole (methimazole)." King Pharmaceuticals Inc, Bristol, TN.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181622, 'Penbutolol', 'Methimazole', 'Moderate', 'The clearance of some beta-blockers with high extraction ratios may be reduced when a euthyroid state is achieved after the addition of antithyroid agents. Sotalol, nadolol, carteolol, and atenolol, which are primarily eliminated by the kidney, do not appear to be affected by changes in thyroid function.', NULL, 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed.', NULL, 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed. Beta blocker dose reductions may be required.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181836/', '[1] Aro A, Anttila M, Korhonen T, Sundquist H "Pharmacokinetics of propranolol and sotalol in hyperthyroidism." Eur J Clin Pharmacol 21 (1982): 373-7[2] Feely J, Stevenson IH, Crooks J "Increased clearance of propranolol in thyrotoxicosis." Ann Intern Med 94 (1981): 472-4[3] Feely J, Crooks J, Stevenson IH "The influence of age, smoking and hyperthyroidism on plasma propranolol steady state concentration." Br J Clin Pharmacol 12 (1981): 73-8[4] Feely J, Crooks J, Stevenson IH "Plasma propranolol steady state concentrations in thyroid disorders." Eur J Clin Pharmacol 19 (1981): 329-33[5] Hallengren B, Nilsson OR, Karlberg BE, Melander A, Tegler L, Wahlin-Boll E "Influence of hyperthyroidism on the kinetics of methimazole, propranolol, metoprolol and atenolol." Eur J Clin Pharmacol 21 (1982): 379-84', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181623, 'Phenobarbital', 'Methimazole', 'Minor', 'Studies of rats have suggested that phenobarbital may cause a striking increase in the uptake and oxidation of methimazole in the thyroid. The clinical implications of this finding for humans is unknown.', NULL, '-', NULL, '-', 'Metabolism, Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181837/', '[1] Lees JF, Alexander WD "Effect of phenobarbitone on metabolism of methimazole." Lancet 1 (1973): 616', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181624, 'Prednisolone', 'Methimazole', 'Moderate', 'The clearance of prednisolone may be increased when coadministered with some antithyroid agents, such as carbimazole and methimazole. The mechanism for this interaction has not been delineated, but may involve induction of CYP450-mediated metabolism of prednisolone.', NULL, 'Patients should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when carbimazole or methimazole are added, discontinued, or the dosage is changed.', NULL, 'Patients should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when carbimazole or methimazole are added, discontinued, or the dosage is changed. Prednisolone dosage adjustments may be required. Patients should be advised to notify their clinician if they experience a worsening or change in their symptoms.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181838/', '[1] Legler UF "Impairment of prednisolone disposition in patients with Graves'' disease taking methimazole." J Clin Endocrinol Metab 66 (1988): 221-3[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365209);
