INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181919, 'Idelalisib', 'Fenoprofen', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182133/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181920, 'Iloprost', 'Fenoprofen', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182134/', '[1] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181921, 'Human immunoglobulin G (intravenous and subcutaneous)', 'Fenoprofen', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182135/', '[1] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998. Available from: URL: http://www.cdc.gov/mmwr/preview/mmwr