INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181951, 'Fenoprofen', 'Streptokinase', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182165/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181952, 'Fenoprofen', 'Streptomycin', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182166/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767365209);
