INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182097, 'Histamine', 'Fexofenadine', 'Moderate', 'Antihistamines may suppress the patient''s response to histamine phosphate. The length of suppression varies and is dependent on the patient, type of antihistamine, and the duration of antihistamine therapy. The duration of suppression may range from 24 hours with chlorpheniramine or up to several weeks with second-generation antihistamines.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering histamine phosphate to patients treated with antihistamines.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering histamine phosphate to patients treated with antihistamines.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182311/', '[1] "Product Information. Histatrol (histamine phosphate)." Center Laboratories, Port Washington, NY.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363026);
