INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182379, 'Flecainide', 'Abarelix', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182593/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182380, 'Ammonium chloride', 'Flecainide', 'Minor', 'Agents that acidify the urine enhance the renal elimination of flecainide. The result may be decreased plasma flecainide levels and decreased effectiveness. The antiarrhythmic effectiveness of flecainide should be monitored, and plasma flecainide levels should be checked as appropriate. Also, the risk of flecainide toxicity is increased when an agent that acidifies the urine is discontinued.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182594/', '[1] Hertrampf R, Gundert-Remy U, Beckmann J, et al "Elimination of flecainide as a function of urinary flow rate and pH." Eur J Clin Pharmacol 41 (1991): 61-3', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182381, 'Amoxapine', 'Flecainide', 'Moderate', 'Coadministration with Class IC antiarrhythmic agents may increase the plasma concentrations of some tricyclic antidepressants (TCAs). The proposed mechanism is inhibition of CYP450 2D6, the isoenzyme primarily or partially responsible for the metabolism of most TCAs. Class IC antiarrhythmic agents can cause prolongation of the QT interval. Theoretically, coadministration with other agents such as TCAs that can prolong the QT interval may increase the risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', NULL, 'Caution and clinical monitoring are recommended if a class IC antiarrhythmic agent is prescribed in combination with a tricyclic antidepressant.', NULL, 'Caution and clinical monitoring are recommended if a class IC antiarrhythmic agent is prescribed in combination with a tricyclic antidepressant. Pharmacologic response and serum TCA levels should be monitored more closely whenever a class IC antiarrhythmic agent is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary. Patients should be advised to notify their physician if they experience possible signs and symptoms of TCA toxicity such as excessive sedation, dry mouth, blurred vision, urinary retention, constipation, tachycardia, arrhythmia, and seizures. Patients should seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182595/', '[1] "Product Information. Sinequan (doxepin)." Roerig Division, New York, NY.[2] Katz MR "Raised serum levels of desipramine with the antiarrhythmic propafenone ." J Clin Psychiatry 52 (1991): 432-3[3] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77', 1767363026);
