INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182386, 'Flecainide', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6. The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', NULL, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182600/', '[1] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc, Durham, NC.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182387, 'Flecainide', 'Boceprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase the plasma concentrations of certain antiarrhythmic agents including flecainide, lidocaine, and propafenone. The mechanism of interaction has not been described. The interaction has not been specifically studied, but could conceivably lead to serious and/or life-threatening reactions including cardiac arrhythmias and other toxicities if levels are significantly increased.', NULL, 'Caution is advised if boceprevir or telaprevir must be used with flecainide, lidocaine, or propafenone.', NULL, 'Caution is advised if boceprevir or telaprevir must be used with flecainide, lidocaine, or propafenone. Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182601/', '[1] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182388, 'Flecainide', 'Carteolol', 'Moderate', 'Beta-blockers and flecainide may have additive negative inotropic effects. Areas under the curve were increased for both drugs and negative inotropic effects occurred when flecainide and propranolol were given to normal subjects. A case of bradycardia, atrioventricular block and cardiac arrest has been reported after sotalol was added to flecainide.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Patients should be advised to promptly report symptoms such as dizziness, slow or irregular heartbeats, syncope, or palpitations.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182602/', '[1] Lewis GP, Holtzman JL "Interaction of flecainide with digoxin and propranolol." Am J Cardiol 53 (1984): b52-7[2] Minish T, Herd A "Symptomatic bradycardia secondary to interaction between topical timolol maleate, verapamil, and flecainide: a case report." J Emerg Med 22 (2002): 247-9[3] Holtzman JL, Kvam DC, Berry DA, et al "The pharmacodynamic and pharmacokinetic interaction of flecainide acetate with propranolol: effects on cardiac function and drug clearance." Eur J Clin Pharmacol 33 (1987): 97-9[4] Myerburg RJ, Kessler KM, Cox MM, et al "Reversal of proarrhythmic effects of flecainide acetate and encainide hydrochloride by propranolol." Circulation 80 (1989): 1571-9[5] Warren R, Vohra J, Hunt D, Hamer A "Serious interactions of sotalol with amiodarone and flecainide." Med J Aust 152 (1990): 277', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182389, 'Frangula purshiana bark', 'Flecainide', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182603/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767363026);
