INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182452, 'Flecainide', 'Telaprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase the plasma concentrations of certain antiarrhythmic agents including flecainide, lidocaine, and propafenone. The mechanism of interaction has not been described. The interaction has not been specifically studied, but could conceivably lead to serious and/or life-threatening reactions including cardiac arrhythmias and other toxicities if levels are significantly increased.', NULL, 'Caution is advised if boceprevir or telaprevir must be used with flecainide, lidocaine, or propafenone.', NULL, 'Caution is advised if boceprevir or telaprevir must be used with flecainide, lidocaine, or propafenone. Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182666/', '[1] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363026);
