    ) VALUES (1432971, 'Acalabrutinib', 'Dalfopristin', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme. Increased acalabrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', NULL, 'The dosage of acalabrutinib should be reduced to 100 mg once daily when coadministered with moderate CYP450 3A4 inhibitors.', NULL, 'The dosage of acalabrutinib should be reduced to 100 mg once daily when coadministered with moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
