        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1433066, 'Eszopiclone', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of both zopiclone and its pharmacologically active S(-) enantiomer, eszopiclone. Zopiclone has been shown in vitro to be metabolized by CYP450 3A4 and CYP450 2C8, while eszopiclone is primarily metabolized by CYP450 3A4 and 2E1 via demethylation and oxidation.', NULL, 'Caution is advised when zopiclone or eszopiclone is coadministered with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised when zopiclone or eszopiclone is coadministered with moderate CYP450 3A4 inhibitors. A dosage reduction may be required if an interaction is suspected. Patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them, preferably at least 12 hours after administration of the hypnotic.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
