        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1433070, 'Etravirine', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 2C19, 2C9, and/or 3A4 may increase the plasma concentrations of etravirine, which is a substrate of these isoenzymes.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with CYP450 2C19, 2C9, and/or 3A4 inhibitors.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with CYP450 2C19, 2C9, and/or 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
