    ) VALUES (1433127, 'Dalfopristin', 'Lumateperone', 'Major', 'Coadministration with strong or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of lumateperone, which is metabolized by the isoenzyme in vitro.', NULL, 'Coadministration of strong or moderate CYP450 3A4 inhibitors with lumateperone should be avoided.', NULL, 'Coadministration of strong or moderate CYP450 3A4 inhibitors with lumateperone should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
