    ) VALUES (1433192, 'Dalfopristin', 'Rimegepant', 'Moderate', 'INTERVAL: Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of rimegepant, which is primarily metabolized by the isoenzyme. Coadministration with a weak CYP450 3A4 inhibitor is not expected to have a clinically significant effect on rimegepant exposures.', NULL, 'Following concomitant administration with a moderate CYP450 3A4 inhibitor, the manufacturer recommends avoiding another dose of rimegepant within 48 hours.', NULL, 'Following concomitant administration with a moderate CYP450 3A4 inhibitor, the manufacturer recommends avoiding another dose of rimegepant within 48 hours.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
