INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182564, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Prednisolone', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', NULL, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182778/', '[1] "Product Information. Adenovirus Vaccine Type 4 and Type 7 (adenovirus vaccine, live)." Teva Pharmaceuticals USA, North Wales, PA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182565, 'Aluminum hydroxide', 'Prednisolone', 'Minor', 'Antacids and agents with acid-neutralizing effects may impair the absorption of dexamethasone, prednisolone, prednisone, and other corticosteroids, although data from published studies are somewhat conflicting. The mechanism of interaction and clinical significance are unknown. No particular intervention is necessary during concomitant therapy with these agents, but clinicians should be aware of the potential for interaction.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182779/', '[1] Naggar VF, Khalil SA, Gouda MW "Effect of concomitant administration of magnesium trisilicate on GI absorption of dexamethasone in humans." J Pharm Sci 67 (1978): 1029-30[2] Tanner AR, Caffin JA, Halliday JW, Powell LW "Concurrent administration of antacids and prednisone: effect on serum levels of prednisolone." Br J Clin Pharmacol 7 (1979): 397-400[3] Albin H, Vincon G, Demotes-Mainard F, et al "Effect of aluminium phosphate on the bioavailability of cimetidine and prednisolone." Eur J Clin Pharmacol 26 (1984): 271-3', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182566, 'Ambenonium', 'Prednisolone', 'Moderate', 'Corticosteroids and adrenocorticotropic agents may diminish the therapeutic effects of acetylcholinesterase inhibitors in myasthenia gravis. The mechanism of interaction is unknown. Marked deterioration in muscle strength has been reported in patients with myasthenia gravis shortly after the initiation of corticosteroid therapy, particularly when high dosages were used.', NULL, 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis.', NULL, 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis. Respiratory support should be available, and the dosage should be increased stepwise as tolerated (approximately 5 mg/day of prednisone or equivalent at 2- to 3-day intervals until marked clinical improvement or a dosage of 50 mg/day is reached). Dose reductions of the acetylcholinesterase inhibitor may be required as symptoms improve, which often may be delayed and gradual.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182780/', '[1] Patten BM, Oliver KL, Engel WK "Adverse interaction between steroid hormones and anticholinesterase drugs." Neurology 24 (1974): 442-9[2] Brunner NG, Namba T, Grob D "Corticosteroids in management of severe, generalized myasthenia gravis." Neurology 22 (1972): 603-10[3] Namba T "Corticotropin therapy in patients with myasthenia gravis." Arch Neurol 26 (1972): 144-50[4] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[5] Millikan CH, Eaton LM "Clinical evaluation of ACTH and cortisone in myasthenia gravis." Neurology 1 (1951): 145-52', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182567, 'Aminophylline', 'Prednisolone', 'Moderate', 'The concomitant use of theophylline and corticosteroids may theoretically increase the risk of hypokalemia due to additive potassium-lowering effects. Additionally, theophylline serum concentrations may be altered.', NULL, 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered.', NULL, 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered. Patients should be advised to notify their physician if they experience signs of hypokalemia (e.g., weakness, lethargy, and muscle pains or cramps), worsening respiratory symptoms, or signs of theophylline toxicity (e.g., nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182781/', '[1] Fergusson RJ, Scott CM, Rafferty P, Gaddie J "Effect of prednisolone on theophylline pharmacokinetics in patients with chronic airflow obstruction." Thorax 42 (1987): 195-8[2] Buchanan N, Hurwitz S, Butler P "Asthma - a possible interaction between hydrocortisone and theophylline." S Afr Med J 56 (1979): 1147-8[3] Leavengood DC, Bunker-Soler AL, Nelson HS "The effect of corticosteroids on theophylline metabolism." Ann Allergy 50 (1983): 249-51[4] Elvey SM, Saccar CL, Rocci ML, Mansmann HC, Martynec DM, Kester MB "The effect of corticosteroids on theophylline metabolim in asthmatic children." Ann Allergy 56 (