INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182594, 'Digitoxin', 'Prednisolone', 'Moderate', 'Systemically administered corticosteroids may induce hypokalemia and possibly increase the risk of digoxin toxicity. In addition, corticosteroid-induced sodium and water retention can result in edema leading to heart failure. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) which have greater mineralocorticoid activity.', NULL, 'Electrolyte levels should be monitored carefully in patients who must receive concomitant therapy.', NULL, 'Electrolyte levels should be monitored carefully in patients who must receive concomitant therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182808/', '[1] Hardman JG, Gilman AG, Limbird LE eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 9th ed." New York, NY: McGraw-Hill (1995):[2] "Product Information. Hydeltrasol (prednisolone)." Merck & Co, Inc, West Point, PA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182595, 'Doxacurium', 'Prednisolone', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants. The mechanism is unknown. In addition, the risk of myopathy may be increased after long-term use of neuromuscular blocking agents and corticosteroids.', NULL, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', NULL, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy. Dose adjustments of the muscle relaxant may be required. The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182809/', '[1] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[2] "Product Information. Norcuron (vecuronium)." Organon, West Orange, NJ.[3] Laflin MJ "Interaction of pancuronium and corticosteroids." Anesthesiology 47 (1977): 471-2[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Parr SM, Galletly DC, Robinson BJ "Betamethasone-induced resistance to vercuronium: a potential problem in neurosurgery." Anaesth Intensive Care 19 (1991): 103-5[6] "Product Information. Zemuron (rocuronium)." Organon, West Orange, NJ.[7] Meyers EF "Partial recovery from pancuronium neuromuscular blockade following hydrocortosone administration." Anesthesiology 46 (197