INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182688, 'Tolmetin', 'Prednisolone', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', NULL, 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', NULL, 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182902/', '[1] Wilcox CM, Shalek KA, Cotsonis G "Striking prevalence of over-the-counter nonsteroidal anti- inflammatory drug use in patients with upper gastrointestinal hemorrhage." Arch Intern Med 154 (1994): 42-6[2] Stewart JT, Pennington CR, Pringle R "Anti-inflammatory drugs and bowel perforations and haemorrhage." Br Med J 290 (1985): 787-8[3] Messer J, Reitman D, Sacks HS, et al "Association of adrenocorticosteroid therapy and peptic-ulcer disease." N Engl J Med 309 (1983): 21-4[4] Kaufman DW, Kelly JP, Sheeha