INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182770, 'Fluconazole', 'Copanlisib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when copanlisib is used with CYP450 3A4 inhibitors. Patients should be monitored for potentially increased adverse effects such as nausea, vomiting, diarrhea, stomatitis, hyperglycemia, hypertension, noninfectious pneumonitis, cutaneous reactions (e.g., exfoliative dermatitis, maculopapular rash), anemia, neutropenia, thrombocytopenia, and infections. Dosage adjustments for copanlisib may be necessary and should be done in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182984/', '[1] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Aliqopa (copanlisib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767363026);
