INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182858, 'Fluconazole', 'Troleandomycin', 'Moderate', 'Coadministration with fluconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4-mediated metabolism by fluconazole, a moderate inhibitor of the isoenzyme.', NULL, 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluconazole is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183072/', '[1] Sinofsky FE, Pasquale SA "The effect of fluconazole on circulating ethinyl estradiol levels in women taking oral contraceptives." Am J Obstet Gynecol 178 (1998): 300-4[2] Assan R, Fredj G, Larger E, Feutren G, Bismuth H "FK 506/fluconazole interaction enhances FK 506 nephrotoxicity." Diabete Metab 20 (1994): 49-52[3] Ulivelli M, Rubegni P, Nuti D, Bartalini S, Giannini F, Rossi S "Clinical evidence of fluconazole-induced carbamazepine toxicity." J Neurol 251 (2004): 622-3[4] "Product Information. Diflucan (fluconazole)." Roerig Division, New York, NY.[5] Michalets EL, Williams CR "Drug interactions with cisapride: clinical implications." Clin Pharmacokinet 39 (2000): 49-75[6] Canafax DM, Graves NM, Hilligoss DM, et al "Interaction between cyc