INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182964, 'Fludrocortisone', 'Brexucabtagene autoleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel may interfere with the therapeutic effects of brexucabtagene autoleucel. The mechanism for this interaction has not been delineated. Administration of systemic corticosteroids in an effort to manage brexucabtagene autoleucel-related toxicities does not affect the expansion and persistence of cells.', NULL, 'The prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel is not recommended.', NULL, 'The prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel is not recommended. However, systemic corticosteroids may be used for the management of brexucabtagene autoleucel-related adverse effects, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of cells.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183178/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182965, 'Fludrocortisone', 'Bromfenac', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', NULL, 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', NULL, 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183179/', '[1] Wilcox CM, Shalek KA, Cotsonis G "Striking prevalence of over-the-counter nonsteroidal anti- inflammatory drug use in patients with upper gastrointestinal hemorrhage." Arch Intern Med 154 (1994): 42-6[2] Stewart JT, Pennington CR, Pringle R "Anti-inflammatory drugs and bowel perforations and haemorrhage." Br Med J 290 (1985): 787-8[3] Messer J, Reitman D, Sacks HS, et al "Association of adrenocorticosteroid therapy and peptic-ulcer disease." N Engl J Med 309 (1983): 21-4[4] Kaufman DW, Kelly JP, Sheehan JE, Laszlo A, Wiholm BE, Alfredsson L, Koff RS, Shapiro S "Nonsteroidal anti-inflammatory drug use in relation to major upper gastrointestinal bleeding." Clin Pharmacol Ther 53 (1993): 485-94[5] Buchman AL, Schwartz MR "Colonic ulceration associated with the systemic use of nonsteroidal antiinflammatory medication." J Clin Gastroenterol 22 (1996): 224-6[6] Sacanella E, Munoz F, Cardellach F, Estruch R, Miro O, Urbanomarquez A "Massive haemorrhage due to colitis secondary to nonsteroidal anti-inflammatory drugs." Postgrad Med J 72 (1996): 57-8[7] Levy M, Miller DR, Kaufman DW, Siskind V, Schwingl P, Rosenberg L, Strom B, Shapiro S "Major upper gastrointestinal tract bleeding. Relation to the use of aspirin and other nonnarcotic analgesics." Arch Intern Med 148 (1988): 281-5[8] Cantu TG, Lipani JA "Gastrointestinal ulceration with NSAIDs." Am J Med 99 (1995): 440-1[9] Thomas TP "The complications of systemic corticosteroid therapy in the elderly." Gerontology 30 (1984): 60-5[10] Piper JM, Ray WA, Daugherty JR, Griffin MR "Corticosteroid use and peptic ulcer disease: role of nonsteroidal ani-inflammatory drugs." Ann Intern Med 114 (1991): 735-40[11] ReMine SG, McIlrath DC "Bowel perforation in steroid-treated patients." Ann Surg 192 (1980): 581-6', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182966, 'Fludrocortisone', 'Butabarbital', 'Moderate', 'Barbiturates may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by barbiturates.', NULL, 'Patients treated concomitantly with a barbiturate may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, 'Patients treated concomitantly with a barbiturate may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183180/', '[1] Stjernholm MR, Katz FH "Effects of diphenylhydantoin, phenobarbital, and diazepam on the metabolism of methylprednisolone and its sodium succinate." J Clin Endocrinol Metab 41 (1975): 887-93[2] Sehgal VN, Srivastava G "Corticosteroid-unresponsive pemphigus vulgaris following antiepileptic therapy." Int J Dermatol 27 (1988): 258[3] Brooks SM, Werk EE, Ackerman SJ, Sullivan I, Thrasher K "Adverse effects of phenobarbital on corticosteroid metabolism in patients with bronchial asthma." N Engl J Med 286 (1972): 1125-8[4] Hancock KW, Levell MJ "Primidone/dexamethasone interaction." Lancet 2 (1978): 97-8[5] Bartoszek M, Brenner AM, Szefler SJ "Prednisolone and methylprednisolone kinetics in children receiving anticonvulsant therapy." Clin Pharmacol Ther 42 (1987): 424-32[6] Gambertoglio JG, Holford NH, Kapusnik JE, et al "Disposition of total and unbound prednisolone in renal transplant patients receiving anticonvulsants." Kidney Int 25 (1984): 119-23[7] "Product Information. Phenobarbital (phenobarbital)." Lilly, Eli and Company, Indianapolis, IN.[8] Young MC, Hughes IA "Loss of therapeutic control in congenital adrenal hyperplasia due to interaction between dexamethasone and primidone." Acta Paediatr Scand 80 (1991): 120-4[9] Brooks PM, Buchanan WW, Grove M, Downie WW "Effects of enzyme induction on metabolism of prednisolone." Ann Rheum Dis 35 (1976): 339-43[10] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363026);
