INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182977, 'Fludrocortisone', 'Dicoumarol', 'Moderate', 'Corticosteroids and adrenocorticotropic agents may alter the pharmacologic effects of oral anticoagulants. Both increased anticoagulant dosage requirements as well as bleeding and increased anticoagulant sensitivity have been reported during concomitant corticosteroid therapy. Proposed mechanisms include blood hypercoagulability or diminished vascular integrity induced by corticosteroids.', NULL, 'Close monitoring for clinical and laboratory evidence of altered anticoagulant response is recommended during concurrent treatment with corticosteroids or adrenocorticotropic agents.', NULL, 'Close monitoring for clinical and laboratory evidence of altered anticoagulant response is recommended during concurrent treatment with corticosteroids or adrenocorticotropic agents. Patients should be advised to promptly report any signs and symptoms of bleeding or blood clots.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183191/', '[1] Ku LL, Ward CO, Durgin SJ "A clinical study of drug interaction and anticoagulant therapy." Drug Intell Clin Pharm 4 (1970): 300-6[2] Stading JA "Effects of prednisone on the International Normalized Ratio." Am J Health Syst Pharm 63 (2006): 2354-2356[3] Brozovic M, Gurd LJ "Prothrombin during warfarin treatment." Br J Haematol 24 (1973): 579-88[4] Hansen RA, Tu W, Wang J, Ambuehl R, McDonald CJ, Murray MD "Risk of adverse gastrointestinal events from inhaled corticosteroids." Pharmacotherapy 28 (2008): 1325-34[5] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (second of two parts)." N Engl J