    ) VALUES (1433272, 'Idelalisib', 'Rasburicase', 'Moderate', 'The risk and/or severity of diarrhea may be increased when idelalisib is used with other agents that cause diarrhea.', NULL, 'Concomitant use of idelalisib with other drugs that cause diarrhea should be avoided whenever possible.', NULL, 'Concomitant use of idelalisib with other drugs that cause diarrhea should be avoided whenever possible. Patients treated with idelalisib should be monitored for development of severe diarrhea or colitis during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Permanent discontinuation of idelalisib is recommended in those who experience life-threatening diarrhea or intestinal perforation.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
