INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183087, 'Fludrocortisone', 'Trovafloxacin', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183301/', '[1] Khaliq Y, Zhanel GG "Fluoroquinolone-Associated Tendinopathy: A Critical Review of the Literature." Clin Infect Dis 36 (2003): 1404-1410[2] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[3] van der Linden PD, Sturkenboom MC, Herings RM, Leufkens HM, Rowlands S, Stricker BH "Increased risk of achilles tendon rupture with quinolone antibacterial use, especially in elderly patients taking oral corticosteroids." Arch Intern Med 163 (2003): 1801-7[4] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[5] "Product Information. Baxdela (delafloxacin)." Melinta Therapeutics, Lincolnshire, IL.[6] FDA. U.S. Food and Drug Administration "Information for Healthcare Professionals. Fluoroquinolone Antimicrobial Drugs. FDA Alert [7/8/2008]. Available from: URL: http://www.fda.gov/cder/drug/InfoSheets/HCP/fluoroquinolonesHCP.htm." ([7/8/2008]):[7] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183088, 'Fludrocortisone', 'Tubocurarine', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants. The mechanism is unknown. In addition, the risk of myopathy may be increased after long-term use of neuromuscular blocking agents and corticosteroids.', NULL, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', NULL, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy. Dose adjustments of the muscle relaxant may be required. The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183302/', '[1] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[2] "Product Information. Norcuron (vecuronium)." Organon, West Orange, NJ.[3] Laflin MJ "Interaction of pancuronium and corticosteroids." Anesthesiology 47 (1977): 471-2[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Parr SM, Galletly DC, Robinson BJ "Betamethasone-induced resistance to vercuronium: a potential problem in neurosurgery." Anaesth Intensive Care 19 (1991): 103-5[6] "Product Information. Zemuron (rocuronium)." Organon, West Orange, NJ.[7] Meyers EF "Partial recovery from pancuronium neuromuscular blockade following hydrocortosone administration." Anesthesiology 46