INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183381, 'Fluphenazine', 'Morphine (liposomal)', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183595/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183382, 'Fluphenazine', 'Muromonab', 'Moderate', 'The risk of seizures may be increased during coadministration of fluphenazine with any substance that can reduce the seizure threshold, such as aminophylline, antidepressants, antipsychotics, opioids, carbapenems, etc. These agents are often individually epileptogenic and may have additive effects when combined. Many of these agents also exhibit CNS- and/or respiratory-depressant effects, which may be enhanced during their concomitant use with fluphenazine.', NULL, 'Caution is advised if fluphenazine is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, 'Caution is advised if fluphenazine is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections). Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183596/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363026);
