INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183453, 'Flurbiprofen', 'Argatroban', 'Moderate', 'Theoretically, the coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and thrombin inhibitors may potentiate the risk of bleeding. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals.', NULL, 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving thrombin inhibitors.', NULL, 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving thrombin inhibitors. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183667/', '[1] "Product Information. Angiomax (bivalirudin)" The Medicines Compny, Cambridge, MA.[2] "Product Information. Acova (argatroban)" SmithKline Beecham, Philadelphia, PA.[3] "Product Information. Refludan (lepirudin)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183454, 'Flurbiprofen', 'Bedaquiline', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183668/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183455, 'Flurbiprofen', 'Bendroflumethiazide', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', NULL, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', NULL, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183669/', '[1] Muller FO, Schall R, Devaal AC, Groenewoud G, Hundt HKL, Middle MV "Influence of meloxicam on furosemide pharmacokinetics and pharmacodynamics in healthy volunteers." Eur J Clin Pharmacol 4