INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183611, 'Fluticasone', 'Brexucabtagene autoleucel', 'Major', 'Prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel may interfere with the therapeutic effects of brexucabtagene autoleucel. The mechanism for this interaction has not been delineated. Administration of systemic corticosteroids in an effort to manage brexucabtagene autoleucel-related toxicities does not affect the expansion and persistence of cells.', NULL, 'The prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel is not recommended.', NULL, 'The prophylactic use of systemic corticosteroids prior to the administration of brexucabtagene autoleucel is not recommended. However, systemic corticosteroids may be used for the management of brexucabtagene autoleucel-related adverse effects, such as cytokine release syndrome and neurological-related toxicities, without affecting the expansion and persistence of cells.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183825/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183612, 'Fluticasone', 'Echinacea', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183826/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365209);
