INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183656, 'Glyburide', 'Fluvastatin', 'Moderate', 'Coadministration of fluvastatin and glyburide may result in increased plasma concentrations of both drugs. The proposed mechanism is competitive inhibition of CYP450 2C9 metabolism.', NULL, 'Despite the lack of clinical significance reported in the interaction study, pharmacologic responses to fluvastatin and glyburide should be monitored closely during concomitant use, and dosages of the drugs adjusted accordingly.', NULL, 'Despite the lack of clinical significance reported in the interaction study, pharmacologic responses to fluvastatin and glyburide should be monitored closely during concomitant use, and dosages of the drugs adjusted accordingly. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations. The physician should be notified if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183870/', '[1] Appel S, Rufenacht T, Kalafsky G, et al. "Lack of interaction between fluvastatin and oral hypoglycemic agents in healthy subjects and in patients with non-insulin-dependent diabetes mellitus." Am J Cardiol 76 (1995): a29-32[2] Scripture CD, Pieper JA "Clinical pharmacokinetics of fluvastatin." Clin Pharmacokinet 40 (2001): 263-81[3] "Product Information. Lescol (fluvastatin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceutica