INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183724, 'Fluvoxamine', 'Fosaprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183938/', '[1] "Product Information. Emend for Injection (fosaprepitant)." Merck & Company Inc, West Point, PA.[2] "Product Information. Emend (aprepitant)." Merck & Company Inc, West Point, PA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183725, 'Fosphenytoin', 'Fluvoxamine', 'Moderate', 'Coadministration with fluvoxamine may increase the serum concentrations of phenytoin. The proposed mechanism is fluvoxamine inhibition of CYP450 2C9 and 2C19, the isoenzymes responsible for the metabolic clearance of phenytoin.', NULL, 'Caution is advised if phenytoin must be used with fluvoxamine.', NULL, 'Caution is advised if phenytoin must be used with fluvoxamine. Pharmacologic response and serum phenytoin levels should be monitored more closely whenever fluvoxamine is added to or withdrawn from therapy, and the phenytoin dosage adjusted as necessary. The same precaution may be applicable during therapy with other hydantoins, although clinical data are lacking. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183939/', '[1] Mamiya K, Kojima K, Yukawa E, Higuchi S, Ieiri I, Ninomiya H, Tashiro N "Phenytoin intoxication induced by fluvoxamine." Ther Drug Monit 23 (2001): 75-7[2] "Product Information. Luvox (fluvoxamine)." Solvay Pharmaceuticals Inc, Marietta, GA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[5] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[6] Krueger KA, Ga