INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183770, 'Fluvoxamine', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183984/', '[1] "Product Information. Chirocaine (levobupivacaine)" Organon, West Orange, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183771, 'Fluvoxamine', 'Levacetylmethadol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and duration of action of levomethadyl acetate, which is a substrate of the isoenzyme. High plasma levels of levomethadyl acetate may increase the risk of QT interval prolongation, which is thought to be associated with serious cardiac events such as ventricular arrhythmias (e.g., torsade de pointes, ventricular tachycardia), cardiac arrest, and sudden death. Cases of QT prolongation and torsade de pointes have been reported with levomethadyl acetate alone during postmarketing use.', NULL, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183985/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183772, 'Fluvoxamine', 'Lindane', 'Moderate', 'Lindane penetrates human skin and has the potential to cause central nervous system toxicity. Seizures have been reported after excessive use or oral ingestion of lindane. There may be a theoretical risk of increased seizure potential when lindane is used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, theophylline).', NULL, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections). Lindane should be used according to recommended dosage and directions for application.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183986/', '[1] Cox R, Krupnick J, Bush N, Houpt A "Seizures caused by concomitant use of lindane and dextroamphetamine in a child with attention deficit hyperactivity disorder." J Miss State Med Assoc 41 (2000): 690-2[2] Matsuoka LY "Convulsions following application of gamma benzene hexachloride." J Am Acad Dermatol 5 (1981): 98-9[3] "Product Information. Kwell (lindane)." Reed and Carnrick, Jersey City, NJ.[4] Telch J, Jarvis DA "Acute intoxication with lindane (gamma benzene hexachloride)." Can Med Assoc J 126 (1982): 662-3[5] Pramanik AK, Hansen RC "Transcutaneous gamma benzene hexachloride absorption and toxicity in infants and children." Arch Dermatol 115 (1979): 1224-5[6] Ramchander V, Cameron ES, Reid HF "Lindane toxicity in an infant." West Indian Med J 40 (1991): 41-3[7] Tenenbein M "Seizures after lindane therapy." J Am Geriatr Soc 39 (1991): 394-5[8] Solomon BA, Haut SR, Carr EM, Shalita AR "Neurotoxic reaction to lindane in an HIV-seropositive patient: an old medication''s new problem." J Fam Pract 40 (1995): 291-6[9] Munk ZM, Nantel A "Acute lindane poisoning with development of muscle necrosis." Can Med Assoc J 117 (1977): 1050-4', 1767365209);
