INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183835, 'Fondaparinux', 'Bismuth subsalicylate', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding. Salicylates interfere with the action of vitamin K and induce a dose-dependent alteration in hepatic synthesis of coagulation factors VII, IX and X, occasionally increasing the prothrombin time. While these effects are generally slight for most salicylates (except aspirin) at recommended dosages, they may be of clinical significance when combined with the inhibitory effects of anticoagulants on the clotting cascade. Moreover, salicylates are known to cause dose-related gastrointestinal bleeding, which may be complicated by anticoagulant therapy.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184049/', '[1] Wells PS, Holbrook AM, Crowther NR, Hirsh J "Interactions of warfarin with drugs and food." Ann Intern Med 121 (1994): 676-83[2] Fausa O "Salicylate-induced hypoprothrombinemia: a report of four cases." Acta Med Scand 188 (1970): 403-8[3] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (first of two parts)." N Engl J Med 285 (1971): 487-98[4] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (second of two parts)." N Engl J Med 285 (1971): 547-58[5] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association; (1994):[6] Barrow MV, Quick DT, Cunningham RW "Salicylate hypoprothrombinemia in rheumatoid arthritis with liver disease. Report of two cases." Arch Intern Med 120 (1967): 620-4[7] Weiss HJ, Aledort LM, Kochwa S "The effect of salicylates on the hemostatic properties of platelets in man." J Clin Invest 47 (1968): 2169-80[8] Penning-van Beest F, Erkens J, Petersen KU, Koelz HR, Herings R "Main comedications associated with major bleeding during anticoagulant therapy with coumarins." Eur J Clin Pharmacol 61 (2005): 439-44', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183836, 'Fondaparinux', 'Bromfenac', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during LMWH, heparinoid, or fondaparinux therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs). The development of epidural and spinal hematoma can lead to long-term or permanent paralysis. Theoretically, NSAIDs may potentiate the risk of bleeding complications associated with the use of fondaparinux. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals.', NULL, 'Products containing NSAIDs, especially if given chronically or in high dosages, should preferably be discontinued prior to initiation of fondaparinux therapy.', NULL, 'Products containing NSAIDs, especially if given chronically or in high dosages, should preferably be discontinued prior to initiation of fondaparinux therapy. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184050/', '[1] "Product Information. Arixtra (fondaparinux)." Organon, West Orange, NJ.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183837, 'Fondaparinux', 'Capsicum', 'Minor', 'Theoretically, capsicum may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Animal and limited human data suggest that capsicum may increase fibrinolytic activity, lower plasma fibrinogen levels, increase plasma antithrombin III levels, inhibit platelet aggregation, and prolong bleeding time. In patients who have used capsicum extensively prior to receiving anticoagulation, antiplatelet, or thrombolytic therapy, the potential for an interaction should be considered. Clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184051/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000): 1221-7; quiz 1228-30', 1767365209);
