INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (183963, 'Fosaprepitant', 'Cisapride', 'Major', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of cisapride. The mechanism is inhibition of CYP450 3A4-mediated metabolism by aprepitant. High plasma levels of cisapride have been associated with prolongation of the QT interval, which has led to cases of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, concomitant use of aprepitant or fosaprepitant is considered contraindicated.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, concomitant use of aprepitant or fosaprepitant is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184177/', '[1] "Product Information. Emend (aprepitant)." Merck & Company Inc, West Point, PA.[2] "Product Information. Emend for Injection (fosaprepitant)." Merck & Company Inc, West Point, PA.[3] Lewin MB, Bryant RM, Fenrich AL, Grifka RG "Cisapride-induced long QT interval." J Pediatr 128 (1996): 279-81[4] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[5] Kivisto KT, Lilja TJ, Backman JT, Neuvonen PJ "Repeated consumption of grapefruit juice considerably increases plasma concentrations of cisapride." Clin Pharmacol