INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1434292, 'Rotavirus vaccine', 'Ozanimod', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during ozanimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.', 'DDInter', 1767317476, 'The use of live attenuated vaccines should be avoided during and for up to 3 months after discontinuing ozanimod treatment. If live attenuated vaccine immunizations are required, administration should occur at least 1 month prior to initiation of ozanimod. Patients without a healthcare professional confirmed history of varicella or without documentation of a full course of vaccination against varicella-zoster virus (VZV) should be tested for antibodies to VZV before initiating treatment with ozanimod. For antibody-negative patients, a full course of vaccination with varicella vaccine is recommended prior to treatment with ozanimod. In patients who have recently been vaccinated, ozanimod therapy should be postponed for 4 weeks to allow the full effect of vaccination to occur.', 'Others', 'The use of live attenuated vaccines should be avoided during and for up to 3 months after discontinuing ozanimod treatment.', NULL, NULL, 'Major', 'pharmacodynamic');
