INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184104, 'Foscarnet', 'Trovafloxacin', 'Moderate', 'Limited data suggest that coadministration of foscarnet and ciprofloxacin may potentiate the risk of seizures. Both agents are individually epileptogenic and may have additive effects when combined. Specifically, foscarnet can decrease the seizure threshold by chelating ionized calcium and magnesium, while ciprofloxacin inhibits gamma-aminobutyric acid (GABA).', NULL, 'Caution is advised during coadministration of foscarnet and ciprofloxacin, particularly in elderly patients and those with renal impairment, electrolyte and metabolic abnormalities, or underlying neurologic disorders.', NULL, 'Caution is advised during coadministration of foscarnet and ciprofloxacin, particularly in elderly patients and those with renal impairment, electrolyte and metabolic abnormalities, or underlying neurologic disorders. The same precaution may be applicable during therapy with other quinolones, although clinical data are lacking.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184319/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Zagam (sparfloxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[3] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[5] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[6] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[7] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[8] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[9] Fan-Havard P, Sanchorawala V, Oh J, Moser EM, Smith SP "Concurrent use of foscarnet and ciprofloxacin may increase the propensity for seizures." Ann Pharmacother 28 (1994): 869-72[10] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184105, 'Fosfomycin', 'Bifidobacterium longum infantis', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184320/', '[1] Clarion Brands, Inc. "Florajen Probiotics. Available from: URL: https://www.florajen.com/products/florajen-women." ([2018]):', 1767363026);
