INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1434394, 'Glyburide', 'Semaglutide', 'Moderate', 'Coadministration of a glucagon-like peptide-1 (GLP-1) receptor agonist with an insulin secretagogue (e.g., sulfonylurea, meglitinide) may potentiate the risk of hypoglycemia. GLP-1 receptor agonists lower blood glucose by stimulating insulin secretion and lowering glucagon secretion.', NULL, 'A lower dosage of the insulin secretagogue may be required when used in combination with a GLP-1 receptor agonist.', NULL, 'A lower dosage of the insulin secretagogue may be required when used in combination with a GLP-1 receptor agonist. Blood glucose should be monitored closely, and patients should be counseled to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately and contact their physician. Patients should also be advised to take precautions to avoid hypoglycemia while driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
