INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184505, 'Furosemide', 'Chloral hydrate', 'Moderate', 'Rare cases of flushing, hot flashes, tachycardia, and labile blood pressure have been reported after administration of intravenous furosemide within 24 hours of chloral hydrate. The proposed mechanism is displacement of chloral hydrate''s metabolite, trichloroacetic acid, from protein binding sites which either produces serum pH changes or displaces levothyroxine from its protein binding sites, resulting in increased levels of free levothyroxine. Chloral hydrate derivatives may also interact. Oral furosemide is not expected to interact.', NULL, 'The manufacturer recommends avoiding concomitant therapy.', NULL, 'The manufacturer recommends avoiding concomitant therapy. Consider using an alternative hypnotic drug in patients who require intravenous furosemide.', 'Distribution', 'Trichlormethiazide, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 'Suvorexant, Thiopental, Amobarbital, Butalbital, Eszopiclone, Ramelteon, Scopolamine, Tasimelteon, Methohexital, Lemborexant, Methylphenobarbital, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184722/', '[1] Cerner Multum, Inc "ANVISA Bulário Eletrônico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[2] Pevonka MP, Yost RL, Marks RG, Howell WS, Stewart RB "Interaction of chloral hydrate and furosemide: a controlled retrospective study." Drug Intell Clin Pharm 11 (1977): 332-5[3] "Product Information. Lasix (furosemide)." sanofi-aventis , Bridgewater, NJ.[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Scappaticci KA, Ham JA, Sohn YJ, Miller RD, Dretchen KL "Effects of furosemide on the neuromuscular junction." Anesthesiology 57 (1982): 381-8[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184506, 'Furosemide', 'Chlorpromazine', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Pimavanserin', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Ciclopirox, Darunavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184723/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184507, 'Furosemide', 'Cisapride', 'Major', 'Some diuretics may produce hypokalemia and hypomagnesemia which could potentiate the risk of cisapride-induced arrhythmias in patients receiving both drugs concomitantly. Prolonged QT interval, torsades de pointes, and death have been reported.', NULL, 'Cisapride is contraindicated in patients with uncorrected hypokalemia and hypomagnesemia, such as those taking potassium-wasting diuretics.', NULL, 'Cisapride is contraindicated in patients with uncorrected hypokalemia and hypomagnesemia, such as those taking potassium-wasting diuretics.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, Ciclopirox, Probenecid, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184724/', '[1] "Product Information. Propulsid (cisapride)." Janssen Pharmaceutica, Titusville, NJ.[2] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[3] Lewin MB, Bryant RM, Fenrich AL, Grifka RG "Cisapride-induced long QT interval." J Pediatr 128 (1996): 279-81[4] Offman EM, Freeman DJ, Dresser GK, Munoz C, Bend JR, Bailey DG "Red wine-cisapride interaction: Comparison with grapefruit juice." Clin Pharmacol Ther 70 (2001): 17-23[5] Hill SL, Evangelista JK, Pizzi AM, Mobassaleh M, Fulton DR, Berul CI "Proarrhythmia associated with cisapride in children." Pediatrics 101 (1998): 1053-6[6] "Product Information. Propulsid (cisapride)." Janssen Pharmaceutica, Titusville, NJ.[7] Kivisto KT, Lilja TJ, Ba