INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184508, 'Furosemide', 'Clofibrate', 'Minor', 'Clofibrate may increase the diuretic effect of furosemide by displacing it from its protein-binding sites. A reduction in furosemide dosage may be required, especially in patients with the nephrotic syndrome who have reduced albumin levels. If clofibrate and furosemide are used together, the patient should be monitored more closely for electrolyte imbalances.', NULL, '-', NULL, '-', 'Distribution', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Lomitapide, Bempedoic acid, Colesevelam, Alirocumab', 'Bendroflumethiazide, Trichlormethiazide, Bumetanide, Chlorthalidone, Metolazone, Hydrochlorothiazide, Torasemide, Etacrynic acid, Bumetanide, Tamsulosin, Tinidazole, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184725/', '[1] "Product Information. Atromid-S (clofibrate)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Bridgman JF, Rosen SM, Thorp JM "Complications during clofibrate treatment of nephrotic-syndrome hyperlipoproteinaemia." Lancet 2 (1972): 506-9[3] Prandota J, Pruitt AW "Furosemide binding to human albumin and plasma of nephrotic children." Clin Pharmacol Ther 17 (1975): 159-66', 1767369485);
