INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184511, 'Furosemide', 'Digitoxin', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', NULL, 'Digoxin, potassium and magnesium levels should be followed closely.', NULL, 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Trichlormethiazide, Tamsulosin, Tinidazole, Ascorbic acid, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184728/', '[1] Semple P, Tilstone WJ, Lawson DH "Furosemide and urinary digoxin clearance." N Engl J Med 293 (1975): 612-3[2] Tilstone WJ, Semple PF, Lawson DH, Boyle JA "Effects of furosemide on glomerular filtration rate and clearance of practolol, digoxin, cephaloridine, and gentamicin." Clin Pharmacol Ther 22 (1977): 389-94[3] Whang R, Oei TO, Watanabe A "Frequency of hypomagnesemia in hospitalized patients receiving digitalis." Arch Intern Med 145 (1985): 655-6[4] Macolic V, Vrhovac B "Pharmacokinetics and interactions of digoxin, theophylline and furosemide in diseases with edema." Int J Clin Pharmacol Ther Toxicol 31 (1993): 6-11[5] McAl