INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184522, 'Glycerin', 'Furosemide', 'Moderate', 'The chronic use or abuse of laxatives may potentiate the pharmacologic effects of diuretics. Laxatives can cause significant losses of fluid and electrolytes, including sodium, potassium, magnesium and zinc, and these effects may be additive to those of diuretics.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant use with diuretics, patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, More', 'Alvimopan, Sorbitol, Magnesium sulfate, Methylnaltrexone, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Naldemedine, Prucalopride, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184739/', '[1] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[2] Blumenthal M, Goldberg A, Brinckmann J, eds. "Herbal Medicine: Expanded Commission E Monographs." Newton, MA: Integrative Medicine Communications (2000):[3] Atsmon J, Dolev E "Drug-induced hypomagnesaemia : scope and management." Drug Saf 28 (2005): 763-88[4] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8[5] Leary WP, Reyes AJ "Drug interactions with diuretics." S Afr Med J 65 (1984): 455-61', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184523, 'Guanfacine', 'Furosemide', 'Moderate', 'Use of guanfacine in combination with antihypertensive agents may result in additive pharmacodynamic effects such as hypotension and syncope.', NULL, 'Caution is advised during coadministration of these agents.', NULL, 'Caution is advised during coadministration of these agents. Dosage adjustments may be required and hemodynamic responses should be monitored during concomitant therapy. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'Etacrynic acid, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, More', 'Rauwolfia serpentina root, Reserpine, Deserpidine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184740/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Tenex (guanfacine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184524, 'Haloperidol', 'Furosemide', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Pimavanserin', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Ciclopirox, Probenecid, Tioconazole, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184741/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184525, 'Furosemide', 'Diamorphine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Trichlormethiazide, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 'Nalmefene, Disulfiram, Acamprosate, Naltrexone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184742/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pac