INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184530, 'Iloprost', 'Furosemide', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, Ciclopirox, Vemurafenib, More', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Prasugrel, Desirudin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184747/', '[1] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[2] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Buchdahl RM, Taylor P, Warner JD "Nebulised ribavirin for adenovirus pneumonia." Lancet 2 (1985): 1070-1[5] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184531, 'Indacaterol', 'Furosemide', 'Moderate', 'Coadministration with beta-2 adrenergic agonists may potentiate the hypokalemic effects of potassium-wasting diuretics. Beta-2 agonists can cause clinically significant but usually transient decreases in serum potassium concentrations. Since QT prolongation is a possible side effect of beta-2 agonists, exacerbation of hypokalemia may increase the risk of torsade de pointes and other serious arrhythmias. The interaction may be more likely with systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or concomitant theophylline or corticosteroid therapy.', NULL, 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics.', NULL, 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics. Serum potassium level and cardiovascular status should be monitored, especially if the beta-2 agonist is administered systemically or by nebulizer. Patients should be advised to notify their physician if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heartbeat.', 'Synergism', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Amphotericin B, Clindamycin, Ciclopirox, More', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Cromoglicic acid, Ipratropium, Isoprenaline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184748/', '[1] "Product Information. Proventil (albuterol)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[3] Lipworth BJ, McDevitt DG, Struthers AD "Prior treatment with diuretic augments the hypokalemic and electrocardiographic effects of inhaled albuterol." Am J Med 86 (1989): 653-7[4] "Product Information. Striverdi Respimat (olodaterol)." Boehringer Ingelheim, Ridgefield, CT.[5] "Product Information. Brovana (arformoterol)." Sepracor Inc, Marlborough, MA.[6] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[7] "Product Information. Brethaire (terbutaline)." Novartis Pharmaceuticals, East Hanover, NJ.[8] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184532, 'Indapamide', 'Furosemide', 'Moderate', 'The combination of a thiazide and loop diuretic may produce additive or synergistic effects on diuresis and excretion of electrolytes including sodium, potassium, magnesium, and chloride. Although these agents may be combined therapeutically in some patients with inadequate response to a single agent, the increased risk of dehydration, hypotension, hypokalemia, hypomagnesemia, and hyponatremia should be recognized.', NULL, 'Caution is advised during concomitant use of a thiazide and loop diuretic.', NULL, 'Caution is advised during concomitant use of a thiazide and loop diuretic. Dosages should be titrated slowly and carefully, and electrolytes, BUN, fluid status, blood pressure, and renal function should be monitored regularly. Patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia.', 'Synergism', 'Metolazone, Trichlormethiazide, Chlorthalidone, Bendroflumethiazide, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, More', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Vemurafenib, Darunavir, Tioconazole, Diiodohydroxyquinoline, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184749/', '[1] Leary WP, Reyes AJ "Drug interactions with diuretics." S Afr Med J 65 (1984): 455-61[2] Brater DC, Pressley RH, Anderson SA "Mechanisms of the syne