INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (184973, 'Gemfibrozil', 'Artesunate', 'Moderate', 'Coadministration with potent inhibitors of uridine diphosphate glucuronosyltransferase (UGT) may increase the peak plasma concentration (Cmax) and systemic exposure (AUC) of dihydroartemisinin (DHA), an active metabolite of artesunate and substrate of UGT 1A9 or 2B7. Increased exposure to artesunate may increase the risk of adverse effects such as anemia, increased liver transaminases, thrombocytopenia, hyperbilirubinemia, acute renal failure, and leukocytosis.', NULL, 'Monitoring for adverse reactions is recommended if artesunate is coadministered with potent UGT inhibitors.', NULL, 'Monitoring for adverse reactions is recommended if artesunate is coadministered with potent UGT inhibitors. Dosage adjustments or alternative treatments may be required based on clinical response and tolerance.', 'Others', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, More', 'Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185190/', '[1] "Product Information. Artesunate (artesunate)." Amivas, Frederick, MD.[2] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[3] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[4] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, St