INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (185074, 'Gemifloxacin', 'Perphenazine', 'Moderate', 'Certain quinolones, including gemifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation may increase with increasing plasma concentrations of gemifloxacin, the recommended dosage should not be exceeded, especially in patients with renal or hepatic impairment.', 'Synergism', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Enoxacin, Nalidixic acid, Cinoxacin, Trovafloxacin, Delafloxacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185291/', '[1] Iannini PB, Doddamani S, Byazrova E, Curciumaru I, Kramer H "Risk of torsades de pointes with non-cardiac drugs." BMJ 322 (2001): 46-7[2] Ball P "Quinolone-induced QT interval prolongation: a not-so-unexpected class effect." J Antimicrob Chemother 45 (2000): 557-9[3] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[4] Owens RC "Risk assessment for antimicrobial agent-induced QTc interval prolongation and torsades de