INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (185209, 'Gilteritinib', 'Amoxapine', 'Moderate', 'Gilteritinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with gilteritinib, on days 8 and 15 of cycle 1, and prior to the start of the next two subsequent cycles. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Phenelzine, Duloxetine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Milnacipran, Levomilnacipran, Bupropion, Esketamine', 'Dabrafenib, Acalabrutinib, Pexidartinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185426/', '[1] "Product Information. Xospata (gilteritinib)." Astellas Pharma US, Inc, Deerfield, IL.', 1767369485);
