INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (185265, 'Indinavir', 'Gilteritinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of gilteritinib, which is primarily metabolized by the isoenzyme. Increased exposure to gilteritinib may increase the risk of serious adverse effects such as QT interval prolongation, pancreatitis, liver transaminase and bilirubin elevations, edema, infections, and stomatitis.', NULL, 'Concomitant use of gilteritinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of gilteritinib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. Otherwise, close monitoring for increased adverse effects is advisable, including more frequent ECG monitoring. Gilteritinib treatment should be interrupted or dosage reduced in patients with serious or life-threatening toxicity in accordance with the product labeling.', 'Metabolism', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185482/', '[1] "Product Information. Xospata (gilteritinib)." Astellas Pharma US, Inc, Deerfield, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (185266, 'Irinotecan', 'Gilteritinib', 'Moderate', 'Coadministration with gilteritinib may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the intestinal P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic cation transporter 1 (OCT1) transporters, such as dabigatran, digoxin, rosuvastatin, methotrexate, and metformin.', NULL, 'Until more information is available, caution is advised if gilteritinib is used concomitantly with drugs that are substrates of the intestinal P-gp, BCRP, and/or OCT1 transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Until more information is available, caution is advised if gilteritinib is used concomitantly with drugs that are substrates of the intestinal P-gp, BCRP, and/or OCT1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever gilteritinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Teniposide', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Ibrutinib, Afatinib, Tivozanib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185483/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xospata (gilteritinib)." Astellas Pharma US, Inc, Deerfield, IL.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
